import React, { Component, useEffect, useState } from 'react';
import { render } from 'react-dom';
import axios from 'axios';
import {useParams} from 'react-router-dom';

const ConfirmPago = () => {

    // let {reserva} = useParams();
    // console.log("id reserva", {reserva})
    const { id_reserva } = this.props.match.params;
    console.log({id_reserva});
    
    const [informeR, setInformeR] = useState(null)
    const [informeS, setInformeS] = useState(null)

    const getInformeRes = async (id_reserva) => {
        const res = await axios.get('http://localhost:4000/API/informeResDet/' + id_reserva);
        this.setState({
            informeRes: res.data
        });
    }
    
    useEffect(() =>{

    },[])

    const getInformeResServ = async ({id_reserva}) => {
        const res = await axios.get('http://localhost:4000/API/informeResDetServ/' + id_reserva);
        this.setState({
            informeCont: res.data
        });
    }

    
    return (
        <div className="container justify-content-center align-items-center">                <h1>Pago confirmado</h1>
            <p>Id reserva: {id_reserva}</p>
            {/* <p>info{state.informeRes}</p> */}
            <p>Se pago: ${id_reserva}</p>
            {
                this.state.informeRes.map(reserva => (
                    <div className="col-md-4 p-2" key={reserva.id_reserva}>
                        {reserva.departamento}
                    </div>
                ))
            }
            {
                this.state.informeCont.map(contrato => (
                    <div className="col-md-4 p-2" key={contrato.id_reserva}>
                        {contrato}
                    </div>
                ))
            }
        </div>
    )
    
}

export default ConfirmPago
