import React from "react";
import '../../../assetss/css/Comuna.css';
import { Component } from "react";
import {Form} from 'react-bootstrap';
import axios from 'axios';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure({

});
 
const notifyS = () =>{
    toast.success('Operación realizada con éxito',{
        position: toast.POSITION.TOP_CENTER,
        theme: "colored"
    });
};
const notifyE = () =>{
    toast.error('Operación cancelada',{
        position: toast.POSITION.TOP_CENTER,
        theme: "colored"
    });
};

export default class CondominioEdit extends Component{

    state ={
        nom_cnd:'', 
	    id_com:'', 
        nomCom: []
    }




    handleSubmit = async (e) => {
        e.preventDefault();
        const answer = window.confirm("¿Confirmar creación de este articulo?");
            const newComuna = {
                
                nom_cnd: this.state.nom_cnd,
                id_com: this.state.id_com
            };
            if (answer){
            axios.put('http://localhost:4000/API/condominio', newComuna);
            notifyS();
            this.setState({
                nom_cnd:'', 
                id_com: '',
            });
        } else {
            notifyE();
        }
    }



    onInputChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }

    render(){
        return (
            <React.Fragment>
                <div id="admin-background">
                    <div class="container">
                        <h1 id="create-comuna-title">Ingresar Articulo</h1>
                        <div id="create-comuna-form">
                            <Form className="contact-form" onSubmit={this.handleSubmit}>
                                <Form.Group className="mb-3" controlId="Articulo">
                                    <Form.Label>Codigo de Reserva</Form.Label>
                                    <Form.Control type="number" name="id_rmant" placeholder= "id_reserva" rows={1} value={this.state.id_rmant} required onChange={this.onInputChange}/>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Tipo de usuario</Form.Label>
                                    <Form.Select  value={this.state.id_com} onChange={this.onInputChange} name="id_com" required>
                                    {
                                        this.state.nomCom.map(reg => (
                                        <option  value={reg.id_com}>
                                            {reg.nom_com}
                                        </option>
                                        ))
                                    }
                                    </Form.Select>
                                </Form.Group>

                                <button type="submit" class="btn btn-primary" id="btnCreateComuna">Cambio en Mantencion</button>
                            </Form>
                        </div>
                    </div>        
                </div>
            </React.Fragment>
        )
    }
}
