import React from 'react'
import {Toast} from 'react-bootstrap';
import '../../assetss/css/toast.css';
import 'react-toastify/dist/ReactToastify.css';


function MyNotify({type,titleHeader,title,message,color}) {   
    return (
        <div>
            <Toast className="d-inline-block m-1" bg={type}>
                <Toast.Header>
                    {/* <img src="holder.js/20x20?text=%20" className="rounded me-2" alt="" /> */}
                    <strong className="me-auto">{titleHeader}</strong>
                   {title}
                </Toast.Header>
                <Toast.Body className={'Dark' && {color}}>
                {message}
                </Toast.Body>
            </Toast>
        </div>
    )
}
// colores
// 'Primary',
// 'Secondary',
// 'Success',
// 'Danger',
// 'Warning',
// 'Info',
// 'Light',
// 'Dark',

export default MyNotify
