import React from "react";
import '../../../assetss/css/Comuna.css';
import { Component } from "react";
import {Form} from 'react-bootstrap';
import axios from 'axios';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure({

});
 
const notifyS = () =>{
    toast.success('Operación realizada con éxito',{
        position: toast.POSITION.TOP_CENTER,
        theme: "colored"
    });
};
const notifyE = () =>{
    toast.error('Operación cancelada',{
        position: toast.POSITION.TOP_CENTER,
        theme: "colored"
    });
};

export default class ReservaMantencionNew extends Component{

    state ={
        id_serv: '',
        dur_hra: '',
        dur_min: '',
        cost_adult:'',
        cost_nigno:'',
        cost_3ra:'',
        ubi_partida:'',
        ubi_fin:'',
        alimentacion:'',
        transporte:'',
        servicio:[],
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/API/serviciosExtra');
        this.setState({
            servicio: res.data
        });
    }


    handleSubmit = async (e) => {
        e.preventDefault();
        const answer = window.confirm("¿Confirmar creación de este articulo?");
            const newComuna = {
                id_serv: this.state.id_serv,
                dur_hra: this.state.dur_hra,
                dur_min: this.state.dur_min,
                cost_adult: this.state.cost_adult,
                cost_nigno: this.state.cost_nigno,
                cost_3ra: this.state.cost_3ra,
                ubi_partida: this.state.ubi_partida,
                ubi_fin: this.state.ubi_fin,
                alimentacion: this.state.alimentacion,
                transporte: this.state.transporte,
            };
            if (answer){
            axios.post('http://localhost:4000/API/tour', newComuna);
            notifyS();
            this.setState({
                fec_rmant: "",
                id_usr: "",
                id_dpto: "",
            });
            // window.location.assign("http://localhost:3000/listarcomuna");
        } else {
            notifyE();
        }
    }



    onInputChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }

    render(){
        return (
            <React.Fragment>
                <div id="admin-background">
                    <div class="container">
                        <h1 id="create-comuna-title">Ingresar Articulo</h1>
                        <div id="create-comuna-form">
                            <Form className="contact-form" onSubmit={this.handleSubmit}>
                                <Form.Group className="mb-3" controlId="Articulo">
                                    <Form.Label>Ingrese la fecha</Form.Label>
                                    <Form.Control type="date" name="fec_rmant" rows={1} value={this.state.fec_rmant} required onChange={this.onInputChange}/>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Ingrese el departamento</Form.Label>
                                    <Form.Select  value={this.state.id_dpto} onChange={this.onInputChange} name="id_dpto" required>
                                    {
                                        this.state.depto.map(reg => (
                                        <option  value={reg.id_dpto}>
                                            {reg.dir_dpto} habitacion  {reg.num_dpto}
                                        </option>
                                        ))
                                    }
                                    </Form.Select>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Ingrese el usuario</Form.Label>
                                    <Form.Select  value={this.state.id_usr} onChange={this.onInputChange} name="id_usr" required>
                                    {
                                        this.state.usuario.map(reg => (
                                        <option  value={reg.id_usr}>
                                            {reg.email_usr}
                                        </option>
                                        ))
                                    }
                                    </Form.Select>
                                </Form.Group>
                                <button type="submit" class="btn btn-primary" id="btnCreateComuna">Agregar Articulo</button>
                            </Form>
                        </div>
                    </div>        
                </div>
            </React.Fragment>
        )
    }
}
