import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../../../assetss/css/ListaDeptos.css';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure({

});
 
const notifyS = () =>{
    toast.success('Operación realizada con éxito');
};
const notifyE = () =>{
    toast.error('Operación cancelada');
};

export default class CondominioList extends Component {

    state = {
        Condominio: []
    }

    async componentDidMount() {
        this.getCondominio();
    }

    getCondominio = async () => {
        const res = await axios.get('http://localhost:4000/API/Condominio')
        this.setState({
            Condominio: res.data
        })
    }

    Eliminar = async (Id) => {
        const answer = window.confirm("¿Estas Seguro?");
        if (answer){
            await axios.put('http://localhost:4000/API/Eliminarcondominio/' + Id);
            notifyS();
        } else {
            notifyE();
        }
        this.getCondominio();
    };


    Validar = async (Id) => {
         
        const answer = window.confirm("¿Estas Seguro?");
        if (answer){
            await axios.put('http://localhost:4000/API/validarCondominio/' + Id);
            notifyS();
        } else {
            notifyE();
        }
        this.getCondominio();
    };

    render() {
        return (
            <div className="container-fluid">
                <div className="row">
                    
                    {
                        this.state.Condominio.map(cnd => (
                            <div className="col-sm-3 col-md-6" key={cnd.id_usr}>
                                        <div className="card-body">
                                        <p>
                                          nombre: {cnd.nom_com}
                                        </p>
                                        <p>
                                          Comuna:   {cnd.nom_com}
                                        </p>

                                        </div>
                                    <div className="card-footer">
                                        <button className="btn btn-danger" onClick={() => this.Eliminar(cnd.id_usr)}>
                                            ELIMINAR REGISTRO
                                        </button>
                                    </div>
                                </div>
                        ))
                    }

                        <Link to={"/CondominioNew"} className="btn btn-secondary">
                            <i className="material-icons">INGRESAR CONDOMINIO</i>
                        </Link>
                            
                        <Link to={"/"} className="btn btn-secondary">
                            <i className="material-icons"> EDITAR Condominio</i>
                        </Link>
                    </div>
                </div>
            
        )
    }
}
