import React, { Component } from 'react';
import { render } from 'react-dom';
import axios from 'axios';
import {useParams} from 'react-router-dom';

class ConfirmPago extends Component {

    // let {reserva} = useParams();
    // console.log("id reserva", {reserva})

    state = {
        informeRes: [],
        informeCont: []
    }

    getInformeReserva= async (reserva) => {
        const res = await axios.get('http://localhost:4000/API/informeResDet/' + reserva);
        this.setState({
            informeRes: res.data
        });
    }
    
    getInformeCont= async ({reserva}) => {
        const res = await axios.get('http://localhost:4000/API/informeResDetServ/' + reserva);
        this.setState({
            informeCont: res.data
        });
    }

    render(){
        const { reserva } = this.props.match.params;
        console.log({reserva});
        this.getInformeCont({reserva});
        this.getInformeReserva({reserva});
        return (
            <div className="container justify-content-center align-items-center">
                <h1>Pago confirmado</h1>
                <p>Id reserva: {reserva}</p>
                {/* <p>info{state.informeRes}</p> */}
                <p>Se pago: ${reserva}</p>
                {
                    this.state.informeRes.map(reserva => (
                        <div className="col-md-4 p-2" key={reserva.reserva}>
                            {reserva.departamento}
                        </div>
                    ))
                }
            </div>
        )
    }
}

export default ConfirmPago
