import React, { Component } from "react";
import axios from "axios";


class ComunaWS extends Component{

    state={
        comuna:[]
    }

    async componentDidMount(){
        this.getComunas();
    }

    handleChange = event => {
        this.setState({ id_com: event.target.value });
      }

    getComunas(){
        const res = axios.get('http://localhost:4000/API/comuna')
        // this.setState({Comuna:api.nom_com})
        this.setState({comuna:res.data})
        }
    

    deleteComunas = async(id_com) =>{
        axios.delete('http://localhost:4000/API/comuna/'+id_com);
        this.getComunas();
        }
    

    render(){

        return(
            <React.Fragment>
            <form onSubmit={this.deleteComunas}>
                {this.state.comuna.map(com => <li>{com.nom_com}<button onClick={() => this.deleteComunas(com.id_com)}>Eliminar</button></li>
                )}
            </form>


            </React.Fragment>
        );       

    }
}

export default ComunaWS
