import React from "react";


class Marca extends React.Component{

    state={
        form:{
            "Marca":""
        },
        error:false,
        errorMsg:""
    }

    manejadorSubmit(e){
        e.preventDefault();
    }
    
    manejadorChange = async e=>{
        await  this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        })
        console.log(this.state.form);
    }

    manejadorBoton(){
        console.log("enviado")
    }

    render(){
        return(
            <React.Fragment>
            <div class="container">
                <header class="heading"> Registration-Form</header><hr></hr>
                    <div>
                        <form onSubmit={this.manejadorSubmit}>
                            <input type="text" name="fname" id="fname" placeholder="Ingrese la Marca" class="form-control " onChange={this.manejadorChange}/>
                        </form>
                    </div>
                
                    <div class="btn btn-warning">Agregar</div>
                
            </div>
            </React.Fragment>
        );

    }
}

export default Marca
