import React, {Component, useState, useEffect} from 'react';
import ReactDOM  from 'react-dom';
import { useHistory} from 'react-router-dom';
import axios from 'axios';
import NavBar from './Navbar';
import '../../assetss/css/pago.css'

const PayPalButton = window.paypal.Buttons.driver("react", { React, ReactDOM });

function PaypalCheckout () {
    // const [price, setPrice] = useState(0);
    // let history = useHistory();
    const [pago, setPago] = useState([]);
    const precio = pago.Pago ;
    const id_rva = parseInt(sessionStorage.reserva);
    const [informeR, setInformeR] = useState([])
    const [count, setCount] = useState(false);

    let urlId = {
        reserva: id_rva
    }

    let dolar = (precio/830).toFixed(2);

    const createOrder = (data, actions) => {
        return actions.order.create({
            purchase_units: [{
                amount: {
                value: dolar,
                },
            }]
        });
    };

    useEffect((e)=>{
        // e.preventDefault();
            getMontoPago(urlId);
    },[]);

    const getMontoPago = async (id) =>{
        const res = await axios.post('http://localhost:4000/API/pago/abonoPago/' , id)
        setPago(res.data[0]);
        console.log(res.data);
    }
    
    const onApprove = (data, actions) => {
        return actions.order.capture(handlePay());
    };

    function handlePay(e){
        const newPago ={
            id_rva : id_rva,
            monto_pagado: precio
        }
        axios.post('http://localhost:4000/API/pago', newPago);

        console.log("Pago recibido");
        getInformeRes(urlId);
        setCount(true);
    }
    
    const getInformeRes = async (id) => {
        const res = await axios.post('http://localhost:4000/API/informeResDet/', id )
        let x = (res.data)
        setInformeR(x[0])
    }

    console.log('Log paypal')
    console.log('urlId:', urlId)
    console.log('Monto Pago: ', pago)
    console.log('Precio a pagar:', precio)
    console.log('informe Reserva:',{informeR})
    console.log('Id rva:',id_rva)
    console.log('End log paypal')

    if(count===false){
        return( 
            <div id="body_pago">
                <NavBar/>
                <div className="container" id="container-pago">
                    <h2>El monto que debe abonar es ${dolar}USD</h2>
                    <h2>Equivalente al abono requerido de ${precio} CLP</h2>
                    <PayPalButton
                        createOrder={(data, actions) => createOrder(data, actions)}
                        onApprove={(data, actions) => onApprove(data, actions)}
                    />
                </div>
            </div>
        );
    }
    else {
        return(
            <div id="body_pago">
                <NavBar/>
                <div className="container" id="container-detalle">
                    <h1>Pago recibido</h1>
                        <div id="detalles">
                            <li>Reserva registrada a nombre de: {informeR.Cliente}</li>  
                            <li>Fecha de reserva: {informeR.FechaReserva}</li> 
                            <li>Costo de arriendo: {informeR.Costo_arriendo}</li>
                            <li>Monto pagado: ${informeR.MontoPagado} CLP</li>
                            <li>Conversión a dolar: ${dolar} USD</li>
                            <li>{precio}</li>
                        </div>
                </div>
            </div>
        );
    }
}
// "Idreserva",
// "Departamento",
// "Cliente",
// "Estado reserva",
// "FechaReserva",
// "Costo_total",
// "Costo_arriendo",
// "Costoserviciosextra",
// "CostoMultas",
// "MontoPagado",
// "Detalle_check-in",
// "Detalle_check-out"

export default PaypalCheckout
