import React, { Component } from "react";
import axios from "axios";


class ComunaWS extends Component{

    state={
        comuna:[]
    }

    async componentDidMount(){
        this.getComunas();
    }

    getComunas(){
        axios.get('http://localhost:4000/API/comuna')
        // this.setState({Comuna:api.nom_com})
        .then(res => {
            const comuna = res.data;
            this.setState({comuna});

        })
    }

    render(){

        return(
            <React.Fragment>
            <div>
                <h2 className="text-center">
                    Comunas
                </h2>
            </div>
                {this.state.comuna.map(com =>
                <div key={com.id_com} className="container">
                    <div className="row mt-3 mx-2">
                        <div className="col-6 mt-2">
                            <li>{com.nom_com}</li>
                        </div>
                        <div className="col-6">
                            <button className="float-end btn btn-danger btn-sm">Eliminar</button>
                        </div>
                    
                    </div>
                    
                </div> 
                )}
            </React.Fragment>
        );       

    }
}

export default ComunaWS
