import React from "react";
import { Form } from "react-bootstrap";
import 'react-toastify/dist/ReactToastify.css';
import {toast} from 'react-toastify';
import axios from "axios";

/////Notificaciones pop

const notifyE = () =>{
    toast.error('Correo y/o contraseña inválidos, asegúrese que las credenciales son correctas');
};

const notifyESuspendida = () =>{
    toast.error('Su cuenta se encuentra suspendidad o aún no se sido activada, comuníquese con nosotros a través de los medios proporcionados al pie de la página');
};

const notifyS = () =>{
    toast.success('Inicio de sesión existoso, Bienvenido(a) Turismo Real');
};


export default class InformeGeneral extends React.Component{

    state = {
        informeGeneral:[]
    }

    manejadorChange = async e =>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        })

    }

    RedirigirRegistrar = () =>{
        this.props.history.push("/UsuarioNew");
    }

    manejadorBoton = () =>{
        axios.post("http://localhost:4000/API/informeResGen/" + agno, this.state.form)
        .then(response =>{
            informeGeneral = response.data 
        });
    };

    render(){
        
        toast.configure({
        });
        

        function manejadorSubmit(e){
            e.preventDesafult();
            notifyS();
        }


        return(
                <div id="login-body">
                    <br/>
                    <div className="container" id="login-container">
                        <div id="login">
                            <h1 id="title-login">Acceder a tu cuenta</h1>
                            <Form onSubmit={manejadorSubmit}>
                                <Form.Group className="mb-3" controlId="formGroupEmail">
                                    <Form.Label>Ingrese un año para generar informe</Form.Label>
                                    <Form.Control type="email" placeholder="Ingresa tu email" 
                                    name="agno"
                                    onChange={this.manejadorChange}
                                    />
                                </Form.Group>
                                <Form.Group className="mb-3" controlId="formGroupEmail">
                                    <Form.Label>Informe</Form.Label>
                                    {informeGeneral}
                                </Form.Group>

                            </Form>
                            <button type="submit" class="btn btn-primary mb-3 mt-5 col-12" onClick={this.manejadorBoton}>Iniciar sesión</button>
                            <button type="button" class="btn btn-secondary mb-5 col-12" onClick={this.RedirigirRegistrar}>Registrarse</button>
                        </div>                               
                    </div>
                    <br/>
                </div>
        );
    }
}
