import React, { Component } from "react";
import axios from "axios";


class ComunaWS extends Component{

    state={
        comuna:[]
    }

    async componentDidMount(){
        this.getComunas();
    }

    handleChange = event => {
        this.setState({ id_com: event.target.value });
      }

    getComunas(){
        axios.get('http://localhost:4000/API/comuna')
        // this.setState({Comuna:api.nom_com})
        .then(res => {
            const comuna = res.data;
            this.setState({comuna});
            console.log("Get api", res)

        })
    }

    deleteComunas = event =>{
        event.preventDefault();
        axios.delete('http://localhost:4000/API/comuna/$({this.id_com})')
        .then(res => {
          console.log(res);
          console.log(res.data);
        })
    }    

    render(){

        return(
            <React.Fragment>
            <form onSubmit={this.deleteComunas}>
                <label>Comuna: <input type="text" name="id_com" onChange={this.handleChange}/> </label>
                <button type="submit" className="btn btn-danger">Eliminar</button>
            </form>


            </React.Fragment>
        );       

    }
}

export default ComunaWS
