import React, { Component } from 'react';
import { render } from 'react-dom';
import axios from 'axios';
import {useParams} from 'react-router-dom';

class ConfirmPago extends Component {

    // let {reserva} = useParams();
    // console.log("id reserva", {reserva})

    state= {
        informeRes: '',
        informeCont: ''
    }

    componentDidMount = async (id) => {
        const res = await axios.get('http://localhost:4000/API/informeResDet/' + id);
        this.setState({
            informeRes: res.data
        });
    }
    
    componentDidMount = async (id) => {
        const res = await axios.get('http://localhost:4000/API/informeResDetServ/' + id);
        this.setState({
            informeCont: res.data
        });
    }

    render(){
        const { reserva } = this.props.match.params;
        console.log({reserva});
        return (
            <div className="container justify-content-center align-items-center">
                <h1>Pago confirmado</h1>
                <p>Se pago: ${reserva}</p>
            </div>
        )
    }
}

export default ConfirmPago
