import axios from "axios";
import React from "react";
import Footer from "../../Layouts/Footer";
import { Form } from "react-bootstrap";
import { Component } from "react";

class DeptoNew extends Component{
    constructor(props){
        super(props); 
        this.state ={
            dir_dpto:"", 
            num_dpto:"" , 
            n_amb_dpto:"" , 
            desc_dpto:"" , 
            costo_arri_dpto:"" , 
            img_1_dpto:null , 
            img_2_dpto:null , 
            img_3_dpto:null ,
            img_4_dpto:null, 
            img_5_dpto:null ,
            id_cnd:"" 	
    };
}

onChange=(e)=>{
    this.setState({[e.target.name]: e.target.value});
}
manejadorSubmit(e){
    e.preventDefault();
}
manejadorBoton=(e)=>{
    e.preventDefault();
    
    const{

        dir_dpto , 
	    num_dpto , 
	    n_amb_dpto , 
	    desc_dpto , 
	    costo_arri_dpto , 
	    img_1_dpto , 
	    img_2_dpto , 
	    img_3_dpto ,
	    img_4_dpto , 
	    img_5_dpto ,
        id_cnd 	


    }=this.state;
    axios.post('http://localhost:4000/API/departamento',{
        dir_dpto , 
	    num_dpto , 
	    n_amb_dpto , 
	    desc_dpto , 
	    costo_arri_dpto , 
	    img_1_dpto , 
	    img_2_dpto , 
	    img_3_dpto ,
	    img_4_dpto , 
	    img_5_dpto ,
        id_cnd 	
    }).then((result)=>{});
};

render(){
        
    return(
        <React.Fragment>
                <div id="admin-background">
                    <div class="container">
                        <h1 id="create-comuna-title">Ingresar Departamento</h1>
                        <div id="create-comuna-form">
                            <Form className="contact-form" onSubmit={this.handleSubmit}>
                                <Form.Group className="mb-3" controlId="Articulo">
                                    <Form.Label>IDireccion del departamento</Form.Label>
                                    <Form.Control as="textarea" name="dir_dpto" placeholder= "Calle #1234" rows={1} value={this.state.dir_dpto} required onChange={this.onInputChange}/>
                                </Form.Group>

                                <button type="submit" class="btn btn-primary" id="btnCreateComuna">Agregar Articulo</button>
                            </Form>
                        </div>
                    </div>        
                </div>
        </React.Fragment>
    );

    }

}

export default DeptoNew
